data.Datas:
() =>{
                        let listDatas = [];
                        const parcelas = formDdsDetalhes.querySelectorAll('.parcela');
                        parcelas.forEach(parcela => {
                            const numParc = parcela.querySelector('label');
                            const dataInput = parcela.querySelector('input[type="date"]');
                            const valorInput = parcela.querySelector('input[name="Valor"]');
                            const numPDC = parcela.querySelector('input[name="Num_PDC_parcela"]');
                            if(numParc?.textContent)
                            {
                                const numParcAjust = parseInt(numParc.textContent.match(/\d+/)[0])
                                listDatas.push({
                                    Numero_da_parcela: numParcAjust
                                })
                                if(pgtoAnt)
                                {
                                    listDatas.push({
                                        parcela_criada: true
                                    })
                                }else
                                {
                                    listDatas.push({
                                        parcela_criada: false
                                    })
                                }
                            }
                            if(dataInput?.value){
                                const [ano, mes, dia] = dataInput.value.split('-');
                                listDatas.push({
                                    Vencimento_previsto: `${dia}/${mes}/${ano}`
                                })
                            }
                            if(valorInput?.value){
                                listDatas.push({
                                    Valor: valorInput.value
                                })
                            }
                            if(numPDC?.value)
                            {
                                listDatas.push({
                                    Num_PDC_parcela: numPDC.value
                                })
                            }
                        })
                    }
